import { Component } from '@angular/core';
import {HttpClient} from "@angular/common/http";

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})
export class SignupComponent {

  name: string = "";
  loginname: string = "";
  password: string = "";

  constructor(private  http: HttpClient) {
  }

  save(){
    let bodyData = {
      "name" : this.name,
      "username" : this.loginname,
      "password" : this.password
    };
    this.http.post("http://localhost:8081/api/v1/auth/signup",bodyData,{responseType: 'text'}).subscribe((resultData: any) =>
    {
      console.log(resultData);
      alert("User Registered Successfully");
    });
  }
}
<div class="container mt-4">
  <div class="card">
<form>
  <h2>Sign up page</h2>
  <div class="form-group">
    <label>Name</label>
    <input type="text"
           [(ngModel)]="name" [ngModelOptions]="{standalone: true}"
           class="form-control" id="inputName"  placeholder="Enter name">
  </div>
  <div class="form-group">
    <label>UserName</label>
    <input type="text"
           [(ngModel)]="loginname" [ngModelOptions]="{standalone: true}"
           class="form-control" id="inputLoginName"  placeholder="Enter Login Name">
  </div>
  <div class="form-group">
    <label>Password</label>
    <input type="password"
           [(ngModel)]="password" [ngModelOptions]="{standalone: true}"
           class="form-control" id="inputPassword" placeholder="Enter Password">
  </div>
  <div style="align-self: center">
  <button type="submit" class="btn btn-primary" (click)="save()">Register</button>
  </div>
</form>
</div>
</div>
