import { Component } from '@angular/core';

@Component({
  selector: 'app-login2',
  templateUrl: './login2.component.html',
  styleUrls: ['./login2.component.scss'],
})
export class Login2Component {

  signupCustomerName: string = "";
  signupUserName: string = "";
  signupPassword: string = "";
  loginUserName: string = "";
  loginPassword: string = "";

  signUpUsers: any[] = [];
  signUpObj:any = {
    signUpCustomerName: '',
    signUpUserName: '',
    password: ''
  };
  loginObj:any = {
    loginUserName: '',
    password: ''
  };

  constructor() {
  }

  ngOnInit(): void{
    const localData = localStorage.getItem('signUpUsers');
    if(localData!=null){
      this.signUpUsers = JSON.parse(localData);
    }
  }

  onSingUp(): void{
    this.signUpUsers.push(this.signUpObj);
    localStorage.setItem('signUpUsers', JSON.stringify(this.signUpUsers))
    this.signUpObj = {
      signUpCustomerName: '',
      signUpUserName: '',
      password: ''
    };
  }

  onLogin(): void{
    const isUserExist = this 
  }

}
<!DOCTYPE html>
<html>
<head>
  <title>Slide Navbar</title>
</head>
<body>
<div class="main">
  <input type="checkbox" id="chk" aria-hidden="true">

  <div class="signup">
    <form>
      <label for="chk" aria-hidden="true">Sign up</label>
      <input type="text" name="txt" placeholder="Customer name" required="" [(ngModel)]="signUpObj.signUpCustomerName" >
      <input type="text" name="txt" placeholder="User name" required="" [(ngModel)]="signUpObj.signUpUserName">
      <input type="password" name="pswd" placeholder="Password" required="" [(ngModel)]="signUpObj.password">
      <button (click)="onSingUp()">Sign up</button>
    </form>
  </div>

  <div class="login">
    <form>
      <label for="chk" aria-hidden="true">Login</label>
      <input type="text" name="txt" placeholder="User name" required="" [(ngModel)]="loginObj.loginUserName" >
      <input type="password" name="pswd" placeholder="Password" required="" [(ngModel)]="loginObj.password" >
      <button (click)="onLogin()">Login</button>
    </form>
  </div>
</div>
</body>
</html>
